<?php$file='images/';$res=[];get_files($file,$res);header("Content-type:text/html; charset=utf-8");echo '[';$str='';foreach($res as $v){  if(substr($v,0,1)!='.'){    if(!$str){      $str='"images/'.$v.'"';    }else{      $str=$str.','.'"images/'.$v.'"';    }  }}// $str=rtrim($str,',');echo $str;echo ']';function get_files($path,&$arr,$base=null){	if(!$base)$base=$path;	if(is_dir($path)){		$files=scan_files($path);		if($files){			foreach($files as $v){				if(is_dir($path.'/'.$v)){					$cfiles=get_files($path.'/'.$v,$arr,$base);				}else if(substr($v,0,1)!='.' && is_file($path.'/'.$v)){					$name=str_replace($base.'/','',$path.'/'.$v);					array_push($arr,$name);				}			}		}	}else exit('this is not a dir');}function scan_files($dir){	if(is_dir($dir)){		$files=scandir($dir);		unset($files[0]);unset($files[1]);		return $files;	}else return [];}?>